		{
			opacity: 0;
		}

		&:disabled + i:before,
		&[readonly] + i:before
		{
			opacity: .3;
		}

		&:disabled:checked + i:after,
		&[readonly]:checked + i:after
		{
			opacity: .3;
			+ i:before { .m-faContent(@fa-var-square, .93em); 
			}
			&:checked + i:before { .m-faContent(@fa-var-check-square-o, .93em); 
			}
		}

		&:checked
		{
			& + i:before
			{
				opacity: 0;
			}
			+ i:before { .m-faContent(@fa-var-circle-o, .86em); 
			}
			&:checked + i:before { .m-faContent(@fa-var-check-circle, .86em); 
			}

			& + i:after
			{
				opacity: 1;
			}
		}

		&:focus + i
		{
			&:before,
			&:after
			{
				outline: Highlight solid 2px;
				-moz-outline-radius: 5px;

				@media (-webkit-min-device-pixel-ratio: 0)
				{
					outline: -webkit-focus-ring-color auto 5px;
				}
			}
		}
	}

