.m-transitionFade(@speed: @xf-animationSpeed)
{
	display: none;
	opacity: 0;
	.m-transition(all, -xf-opacity; @speed);
	&.is-active
	{
		display: block;
		opacity: 1;
	}
	&.is-transitioning
	{
		display: block;
	}
}


.m-transitionFadeDown(@speed: @xf-animationSpeed)
{
	.m-transitionFade(@speed);

	overflow-y: hidden;
	height: 0;

	.m-transitionProperty(all, -xf-height;);

	&.is-active
	{
		height: auto;
		overflow-y: visible;
	}

	&.is-transitioning
	{
		overflow-y: hidden;
	}
}

